  computed: {
    experienceYears() {
      const startDate = new Date('2019-11-01');
      const currentDate = new Date();
      const diffTime = Math.abs(currentDate - startDate);
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      const years = Math.floor(diffDays / 365);
      const months = Math.floor((diffDays % 365) / 30);
      
      // –°–∫–ª–æ–Ω–µ–Ω–∏–µ –¥–ª—è "–ª–µ—Ç"
      let yearsText = '–ª–µ—Ç';
      if (years % 10 === 1 && years % 100 !== 11) {
        yearsText = '–≥–æ–¥';
      } else if ([2, 3, 4].includes(years % 10) && ![12, 13, 14].includes(years % 100)) {
        yearsText = '–≥–æ–¥–∞';
      }
      
      // –°–∫–ª–æ–Ω–µ–Ω–∏–µ –¥–ª—è "–º–µ—Å—è—Ü–µ–≤"
      let monthsText = '–º–µ—Å—è—Ü–µ–≤';
      if (months % 10 === 1 && months % 100 !== 11) {
        monthsText = '–º–µ—Å—è—Ü';
      } else if ([2, 3, 4].includes(months % 10) && ![12, 13, 14].includes(months % 100)) {
        monthsText = '–º–µ—Å—è—Ü–∞';
      }
      
      return `${years} ${yearsText} –∏ ${months} ${monthsText}`;
    }
